function saveValue(llave, valor) {

    cc ("key-> " + llave  + " value -> " + valor);

    switch (llave) {
        case "pxp_display":
            localStorage.ppdisplay = valor;
            break;
        case "pxp_top":
            localStorage.pptop = valor;
            break;
        case "pxp_left":
            localStorage.pptop = valor;
            break;
        case "pxp_opacity":
            localStorage.opacity = valor;
            break;
        case "pxp_size":
            localStorage.size = valor;
            break;
        case "NF":
            localStorage.nf = valor;
            break;
        case "PreOpacity":
            localStorage.PreOpacity = valor;
            break;
        case "pxp_hover":
            localStorage.pphover = valor;
            break;
        case "imgPath":
            localStorage.imgPath = valor;
            break;
        default:
             cc("missing : " + llave + " -> " + valor);
    }
}

