function set_opacity(mode){
    if (mode=="plus") {
        opacidad ='+=0.1';
    }else{
        opacidad ='-=0.1';
    }
    $(ppclass + '  img').animate({opacity: opacidad},1, function() {
        saveValue('pxp_opacity', $(ppclass + ' img').css("opacity"));
        Copacity = readValue('pxp_opacity');});
        message ("opacity: " + Copacity );
}

/* ---------------------------------------------------------------------- */


function message (text) {

    $('.messagebox span').html(text );
    $('.messagebox').show();
    clearTimeout(myVar);
    myVar= window.setTimeout(function() { $('.messagebox').hide(); }, 500);

}


/* ---------------------------------------------------------------------- */

function set_size (mode){

    var temp= parseInt($(ppclass + ' img').css("width"));

    if (mode=="plus") {
        temp *= 2;
    }else{
        temp /= 2;
    }

    $(ppclass + '  img').animate({width: temp},1, function() {
        saveValue('pxp_size', $(ppclass + ' img').css("width"));
        message ("size: " + $(ppclass + ' img').css("width"));
     });
}

/* ---------------------------------------------------------------------- */

function reset_opacity(){
    $(ppclass + ' img').css("opacity","1");
    saveValue('pxp_opacity', $(ppclass + ' img').css("opacity"));
    message ("opacity: " + $(ppclass + ' img').css("opacity"));

}

/* ---------------------------------------------------------------------- */

function reset_size(){
    $(ppclass + ' img').css("width","");
    saveValue('pxp_size', $(ppclass + ' img').css("width"));
    message ("size: " + $(ppclass + ' img').css("width"));

}
/* ---------------------------------------------------------------------- */

function reset(){
    saveValue('pxp_top', 0);saveValue('pxp_left', 0);$( ppclass + ' img').animate({top: '0',left: '0'},300);
}

/* ---------------------------------------------------------------------- */

function move_to(direction){
    switch (direction) {
        case "up":    $(ppclass + '  img').animate({top: '-=1',},1); saveValue('pxp_top',  $(ppclass + ' img').css("top"));  break;
        case "right": $(ppclass + '  img').animate({left: '+=1',},1);saveValue('pxp_left', $(ppclass + ' img').css("left")); break;
        case "down":  $(ppclass + '  img').animate({top: '+=1',},1); saveValue('pxp_top',  $(ppclass + ' img').css("top"));  break;
        case "left":  $(ppclass + '  img').animate({left: '-=1',},1);saveValue('pxp_left', $(ppclass + ' img').css("left")); break;
    }
}

