function setup(){
    saveValue('NF','');
    if (readValue('pxp_display')) {Cdisplay = readValue('pxp_display');} else {Cdisplay = 'block';}
    if (readValue('pxp_top')) {Ctop = readValue('pxp_top');} else {Ctop = 0;}
    if (readValue('pxp_left')) {Cleft = readValue('pxp_left');} else {Cleft = 0;}
    if (readValue('pxp_opacity')) {Copacity = readValue('pxp_opacity');} else {Copacity = 0.4;}
    if (readValue('pxp_size')) {Cwidth = readValue('pxp_size');} else {Cwidth = '';}
    if (!readValue('PreOpacity')) {saveValue('PreOpacity',0.4)}
    if(!readValue('pxp_hover')) {saveValue('pxp_hover',0);}
    if (readValue('imgPath')) {
        imagePath =readValue('imgPath');
        check_for_image(imagePath,0);

    } else {Create_Dialog(msg);}
}

