function image_toggle(l){

    var temp = readValue('imgPath');
    var ext = temp.substring(temp.length-4, temp.length);
    var name = temp.substring(0, temp.length-4);
    var buscar="";

    if (l!=0) {
        buscar= name + l + ext;
    }else {
        buscar= name +  ext;
    }

    imagenumber=l;
    $(ppclass + ' img').attr("src", buscar);
    $('.pxp_slide_missing h2').html(imagenumber );
    $('.pxp_slide_missing').show();
    clearTimeout(myVar);
    myVar= window.setTimeout(function() { $('.pxp_slide_missing').hide(); }, 1000);

}

